mapper.update:

update notice
set type = #{type,jdbcType=VARCHAR},
  status = #{status,jdbcType=VARCHAR},
  content = #{content,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select user_id as userId,content as postId , create_time as createTime from notice where type = #{type}  and content in (
    select id from post where user_id=#{userId}
) order by create_time desc

select content as userId,content as postId , create_time as createTime from notice where type = #{type}
                                                                                     and  user_id = #{userId}
 order by create_time desc

select user_id as userId,content as postId , create_time as createTime from notice where type = #{type}     and content in (
  select id from post where user_id=#{userId}
) order by create_time desc

select user_id as userId,content as postId , create_time as createTime from notice where type = #{type}  and content in (
  select id from post where user_id=#{userId}
) order by create_time desc

mapper.update:
update notice set status=1 where  type = #{type} and content in (
    select id from post where user_id=#{userId}
  )

mapper.select:
select count(1) from notice where type = #{type} and status=0 and content in (
  select id from post where user_id=#{userId}
) order by create_time desc
