mapper.insert:

insert into post (id, user_id, content, 
  type, tag, likes, comments, 
  focus, create_time,img_url
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{content,jdbcType=VARCHAR}, 
  #{type,jdbcType=VARCHAR}, #{tag,jdbcType=VARCHAR}, #{likes,jdbcType=INTEGER}, #{comments,jdbcType=INTEGER}, 
  #{focus,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, #{imgUrl,jdbcType=VARCHAR}
  )

mapper.select:
select * from post where 1=1 and content like concat('%',#{content},'%')
and type =#{type}
order by likes desc,create_time desc
